import React from 'react' 
import M from "materialize-css";

class Carrusel extends React.Component{

    componentWillMount() {

       
         
        document.addEventListener('DOMContentLoaded', function() {
            let elems = document.querySelectorAll('.carousel');
            let elemsSidebar = document.querySelectorAll('.sidenav');   


            
            let instances = M.Carousel.init(elems, {
              shift: 100,
              fullWidth: false,
              indicators: true
            } );
            let instancesSidebar = M.Sidenav.init(elemsSidebar, {
                            edge:"left", 
                            draggable:true });

          });    
          
          
          document.addEventListener('DOMContentLoaded', function() {
            let elemsCollapsible = document.querySelectorAll('.collapsible');
            let instances = M.Collapsible.init(elemsCollapsible);
          });
        
          
    }
   
    render() {
        return(
            <div className="carousel carousel-slider carrusel-hoteles">

                {
                    this.props.datos.playaCarmen.map(  
                        (objeto) => {

                            return(
                                 <div className="carousel-item" href="#one!">

                                <div className="contenedor-titulo-hotel">
                                                          
                                <h1 className="titular-hero-hotel">
                                <span>{objeto.subtitulo} </span> 
                                <br/>
                                {objeto.titulo}</h1>
                                    </div>
                    
                             <img className="imagenes-del-hero" src={objeto.imagen} />
                    
                            </div>
                            )
                            
                           
                        }
                     ) 
                      
                }

            </div>    
        )
    }
}

export default Carrusel
