                        <Link type = "button" 
                            onClick = {this.props.onSubmit} 
                            to = "/destinos"
                            className ="btn waves-effect waves-light red darken-2"                      
                            name="action">Siguiente >
                            
                            <i className ="material-icons right">send</i>
                        </Link>
                    </form>
                </div>
             </div>   

            )

    }
   
}

export default Saludo
