import React from 'react' 
import 'materialize-css/dist/css/materialize.min.css'
import HPrincess from '../img/hotel_princess.jpg'

class Hotel extends React.Component {
    render(){
          return (
              <div className="row">

                  {this.props.datos.PlayaCarmen.map(
                      (tarjeta) => {
                          return (
                            <div className="card col s3">
                                <div className="card-image waves-effect waves-block waves-light">
                                    <img className="activator" src={HPrincess} alt = "imagen del hotel"/>
                                </div>
                            <div className="card-content">
                                <span className="card-title activator grey-text text-darken-4"><strong>Hotel Princess</strong><i className="material-icons right">more_vert</i></span>
                                <p><a href="#">All Suites Resort & Spa</a></p>
                            </div>
                            <div className="card-reveal">
                                <span className="card-title grey-text text-darken-4">Hotel Princess<i class="material-icons right">close</i></span>
                                <p>El lugar ideal para pasar unas exclusivas vacaciones en el caribe...</p>
                            </div>
                            </div>

                          )
                      }
                    

                  ) }

               

              </div>
            
       
          )
}
}

export default Hotel
