import React from 'react'
import TarjetaRick from './tarjetaRick'


class LlamarApi extends React.Component{

    // Debemos manejar los estados de una carga, que son loading, error y datos, los cuales se especifican en el estado del componente

    state = {
        loading:true,
        error:null,
       data: {
           results: []
       }
    }

    componentDidMount() {
        this.fetchCharacters()
    }

    fetchCharacters = async () => {
        // antes de hacer cualquier cosa, especificamos los estdos loading y error del componente
        this.setState({loading:true, error: null})

        try{
            const response = await fetch('https://rickandmortyapi.com/api/character/')
            const data = await response.json()
            this.setState({
                loading:false, 
                data:data
            })

        } catch (error){

            this.setState({
                loading:false, 
                error:error
            })

        }
        
    }
    render() {
        return(
            <div className= 'row'>
                <h1>hola mundo</h1>
                {
                    <div>
                         {this.state.data.results.map(muneco => (
                             <li key= {muneco.id}>
                                 <TarjetaRick data = {muneco}/>
                             </li>
                             
                         ))
                         }
                    </div>
                   
                    
                }
            </div>
        )
    }
}

export default LlamarApi
