class Formulario extends React.Component {

    handleChange = (e) => console.log({value:e.target.value})


    render() {
        return(
            <div className="container">
           <form>
               <label for = "entrada" contenteditable="true" > incluye un texto aca</label>
               <input className= "entrada"
               draggable="true"
                onChange = {this.handleChange}
                tipe= "text"></input>

                <button>sdfasd</button>
               <p>Cuando edites y deselecciones este control, el color de fondo de su contenedor cambiar√°: <input type="text" onchange="changeColor(this.parentNode)"/></p>
           </form>
           </div>
        )
    }
}

export default Formulario
