import React from 'react'
import Formulario from './formulario'
import TarjetaFormulario from './tarjeta-formulario'

class ContenedorFormulario extends React.Component{
    state = { form: {}}

    handleChange = e => {
        this.setState({
            form:{
                ...this.state.form,
                [e.target.name]: e.target.value,
            }
        })
    }

    render() {
        return(
            <div className="row">
                <div className="col s12 l6">
                    <Formulario onChange = {this.handleChange } formValues={this.state.form}/>

                </div>
                <div className="col s12 l6">
                     <TarjetaFormulario datos={this.state} />
                </div>               
               

            </div>
           

        )
    }
}

export default ContenedorFormulario
