import React from 'react' 
import 'materialize-css/dist/css/materialize.min.css'
import Playa from '../img/thumnails/playa_carmen.jpg'
import '../tarjeta_destino.css'

class Destino extends React.Component {
    render(){
          return(

            <div className = "container">
               {this.props.datos.map(
                        (tarjeta) => {
                            return (
                                <div key= {tarjeta.id}>

                                    
                                  <div className="row">
                                    <div className="col s12 m3">
                                      <div className="card">
                                        <div className="card-image">
                                          <img src={Playa} alt= "foto"/>
                                          <span className="card-title"> <strong> {tarjeta.nombre}</strong></span>
                                          <a className="btn-floating halfway-fab waves-effect waves-light red" href="hola"><i className="material-icons">add</i></a>
                                        </div>
                                        <div className="card-content">
                                          <p>{tarjeta.slogan}</p>
                                        </div>
                                      </div>
                                    </div>
                                
                              </div>
                                   

                                </div>
                            )
                        }
                    )}

              
            </div>
 )
    }
       
    
}

export default Destino
