import React from 'react'
import 'materialize-css/dist/css/materialize.min.css'
import './saludo.css'


class Saludo extends React.Component {



    handleChange = (e) => {

        this.setState({
            firstName: e.target.value,
        })
    //      console.log({
    //         name: e.target.name, 
    //         value: e.target.value})
           
    }

    handleClick = (e) => {
        console.log('boton apretado')
    }

    handleSubmit = e => e.preventDefault();


    render() {
        return (

            <div className= "saludo">
                <div className="container center-align">
                    <h1>Hola amigo turista,<br/>¿Cómo te llamas?</h1>
                    <form onSubmit = {this.handleSubmit}>
                        <input onChange= {this.handleChange} type="text" name = "Usuario"></input>
                        <button type = "button" onClick = {this.handleClick} className ="btn waves-effect waves-light red darken-2" type="submit" name="action">Siguiente
                            <i className ="material-icons right">send</i>
                        </button>
                    </form>
                </div>
             </div>   

            )

    }
   
}

export default Saludo
