import React from 'react' 

class Formulario extends React.Component {

    handleChange(e) {
        console.log({
            name: e.target.name,
            value: e.target.value
        })
    }

    render(){
        return(
        <div>
            <form onSubmit>
                <label>ponga un nombre</label>
                <input type = "text" onChange={this.handleChange}></input>
            </form>
        </div>
            )
    } 
}

export default Formulario
