import React from 'react'
import 'materialize-css/dist/css/materialize.min.css'


class Saludo extends React.Component {

    handleChange = (e) => {
         console.log({
            name: e.target.name, 
            value: e.target.value})
    }

    handleClick = (e) => {
        console.log('boton apretado')
    }

    handleSubmit = (e) => {
        e.preventDefault()
    }


    render() {
        return (
                <div>
                    <h1>Hola amigo turista, <br/> ¿Cómo te llamas?</h1>
                    <form onSumbit = {this.handleSubmit}>
                        <input onChange= {this.handleChange} type="text" name = "Usuario"></input>
                        <button onClick = {this.handleClick} className ="btn waves-effect waves-light red darken-2" type="submit" name="action">Siguiente
                            <i className ="material-icons right">send</i>
                        </button>
                    </form>
                </div>
                

            )

    }
   
}

export default Saludo
