import React from 'react'

class Ventajas extends React.Component{
    render() {
        return(
            <div class="row container">
                {
                    this.props.datos.map(
                        (ventajas) => {
                            return(
                                <div class="col s12 m5">
                                    <div class="card-panel teal row">

                                    <i className="material-icons col s12 l2 ">{ventajas.icono}</i>
                                        <span class="white-text col s12 l10">I am a very simple card. I am good at containing small bits of information.
                                            I am convenient because I require little markup to use effectively. I am similar to what is called a panel in other frameworks.
                                        </span>
                                    </div>
                                </div>
                            )
                        }
                    )
                }
                
            </div>
        )
    }
}

export default Ventajas
